///
/// ************************************************************************
/// These calls are used to set up and modify the coercion dispatch table.
/// ************************************************************************
pub type AECoerceDescProcPtr =
    Option<unsafe extern "C-unwind" fn(*const AEDesc, DescType, SRefCon, *mut AEDesc) -> OSErr>;

